import { Project, User, Department, ProjectStatus, ProjectPriority, Task, TaskStatus, Risk, Meeting, Issue, UserRole, RiskStatus, IssueStatus, RiskImpact, TaskRequestStatus, MeetingStatus, TaskPriority, ProjectTemplateType } from '../types';

// Empty Departments
export const mockDepartments: Department[] = [];

// Empty Users
export const mockUsers: User[] = [];

// Empty Projects
export const mockProjects: Project[] = [];

// Empty Tasks
export const mockTasks: Task[] = [];

// Empty Risks
export const mockRisks: Risk[] = [];

// Empty Meetings
export const mockMeetings: Meeting[] = [];

// Empty Issues
export const mockIssues: Issue[] = [];

// Helper function to generate random activities - returns empty array
export const generateRandomActivities = (count: number) => {
  return [];
};

// Empty activities
export const mockActivities = [];

// Empty change requests
export const mockChangeRequests = [];

// Empty task requests
export const mockTaskRequests = [];

// Empty function that returns empty array
const createDefaultTasks = () => {
  return [];
};

// Empty function for default risks
export const createDefaultRisks = () => {
  return [];
};

// Empty function for default issues
export const createDefaultIssues = () => {
  return [];
};

// Empty function for default meetings
export const createDefaultMeetings = () => {
  return [];
};

// Get a mock project reference to use in tasks
const getProjectForTask = (projectId: string): Project => {
  const mockProject: Project = {
    id: projectId || 'mock-project',
    name: 'Mock Project',
    description: 'A mock project',
    status: ProjectStatus.IN_PROGRESS,
    priority: ProjectPriority.MEDIUM,
    startDate: '2023-01-01T00:00:00Z',
    endDate: '2023-12-31T00:00:00Z',
    projectManager: mockUsers[1],
    department: mockDepartments[0],
    progress: 50,
    budget: 100000,
    actualCost: 50000,
    createdAt: '2023-01-01T00:00:00Z',
    updatedAt: '2023-01-01T00:00:00Z',
    templateType: ProjectTemplateType.DEFAULT
  };
  return mockProject;
};

// Mock Notifications
export const mockNotifications = [
  {
    id: 'n1',
    title: 'New Task Assigned',
    message: 'You have been assigned to "Database Implementation" task.',
    type: 'TASK_ASSIGNMENT',
    isRead: false,
    createdAt: '2023-03-10T09:45:00Z',
    userId: 'u2'
  },
  {
    id: 'n2',
    title: 'Meeting Reminder',
    message: 'ERP Implementation Kickoff meeting starts in 1 hour.',
    type: 'MEETING_REMINDER',
    isRead: true,
    createdAt: '2023-02-01T08:00:00Z',
    userId: 'u1'
  },
  {
    id: 'n3',
    title: 'Project Status Update',
    message: 'ERP System Implementation project status changed to "In Progress".',
    type: 'PROJECT_UPDATE',
    isRead: false,
    createdAt: '2023-03-01T10:30:00Z',
    userId: 'u1'
  },
  {
    id: 'n4',
    title: 'Risk Identified',
    message: 'New risk "Data Migration Failure" identified for ERP System Implementation.',
    type: 'RISK_ALERT',
    isRead: false,
    createdAt: '2023-02-05T11:30:00Z',
    userId: 'u1'
  },
  {
    id: 'n5',
    title: 'Task Completed',
    message: 'Task "Requirements Gathering" has been marked as completed.',
    type: 'TASK_UPDATE',
    isRead: true,
    createdAt: '2023-02-16T09:30:00Z',
    userId: 'u1'
  }
];
