                      <Route path="audit-logs" element={<PrivateRoute roleRequired={['ADMIN']}><AuditLogPage /></PrivateRoute>} />
                    </Route>
                    <Route path="*" element={<NotFoundPage />} />
                  </Routes>
                </TaskRequestProvider>
              </TaskProvider>
            </ProjectProvider>
          </AuthProvider>
        </NotificationProvider>
      </MuiThemeProvider>
    </CacheProvider>
  );
};

